def binary_search(numbers: List[int], low: int, high: int, x: int) -> int:
    """Find the rightmost occurrence of x in numbers."""
    if high >= low:
        mid = low + (high - low) // 2
        if (mid == len(numbers) - 1 or x < numbers[mid + 1]) and numbers[mid] == x:
